import React from 'react'
import { connect } from 'react-redux'
import { Field, reduxForm } from 'redux-form'
import { login } from '../../redux/auth-reducer'
import { requiredField } from '../../utils/validators/validators'
import { Input } from '../Common/FormsControls/FormsControls'
import { Redirect } from 'react-router-dom'
import styles from '../Common/FormsControls/FormsControls.module.css'

const LoginForm = (props) => {
    return (
        <form onSubmit={props.handleSubmit}>
            <div>
                <Field
                    placeholder={'Email'}
                    name={'email'}
                    validate={[requiredField]}
                    component={Input} />
            </div>
            <div>
                <Field
                    placeholder={'Password'}
                    name={'password'}
                    type={'password'}
                    validate={[requiredField]}
                    component={Input} />
            </div>
            <div>
                <Field
                    component={Input}
                    name={'rememberMe'}
                    type={'checkbox'} />remember me
            </div>
            {props.error && <div className={styles.formSummaryError}>
                {props.error}
            </div>}
            <div>
                <button type='submit' >Login</button>
            </div>
        </form>
    )
}

const LoginReduxForm = reduxForm({ form: 'login' })(LoginForm)

const Login = (props) => {

    const onSubmitFunction = (formData) => {
        props.login(formData.email, formData.password, formData.remebmerMe)
    }

    if(props.isAuth) {
        return <Redirect to='/profile' />
    }

    return (
        <div>
            <h1>Login</h1>
            <LoginReduxForm onSubmit={onSubmitFunction} />
        </div>
    )
}

const mapStateToProps = (state) => ({
    isAuth: state.auth.isAuth
})

export default connect(mapStateToProps, { login: login })(Login)

// mapDispatchToProps
// {login: loginTC} == {login}
// loginTC - thunk creator
// login - wrapper
