import React from 'react'
import Preloader from '../../Common/Preloader/Preloader'
import s from './ProfileInfo.module.css'
import ProfileStatus from './ProfileStatus'
import ProfileStatusHOC from './ProfileStatusHOC'

const ProfileInfo = (props) => {
    if (!props.profile) {
        return <Preloader />
    }

    return (
        <div>
            {/* <div>
                {<img src='https://www.sciencealert.com/images/2020-04/processed/CanWeGetRonaFromPets_1024.jpg' />}
            </div> */}
            <div className={s.desriptionBlock}>
                <img className={s.avatar} src={props.profile.photos.large || '../../assets/images/user.jpg'} />
                <ProfileStatusHOC status={props.status} updateStatus={props.updateStatus} />
            </div>
        </div>
    )
}

export default ProfileInfo;
